    String postPerformances(Request req, Response res) {
        res.type("application/json");

        if(req.queryParams("imdb") == null || req.queryParams("theater") == null 
        || req.queryParams("date") == null || req.queryParams("time") == null) {
            res.status(400);
            return "Insufficient parameters.";
        }

        String imdb = req.queryParams("imdb");
        String theater = req.queryParams("theater");
        String date = req.queryParams("date");
        String time = req.queryParams("time");
        String title = "";
        int year = 0;
        int seats = 0;

        // Check if theater exists
        String query =
            "SELECT *\n" +
            "FROM   theaters\n" +
            "WHERE  theater = ?\n";

        try (PreparedStatement ps = conn.prepareStatement(query)) {
            ps.setString(1, theater);
            ResultSet rs = ps.executeQuery();
            if(!rs.next()) {
                res.status(404);
                return "No such theater exists.";
            } else {
                seats = rs.getInt("capacity");
            }
        } catch(SQLException e) {
            e.printStackTrace();
        }

        // Check if movie exists
        query =
            "SELECT *\n" +
            "FROM   movies\n" +
            "WHERE  imdbKey = ?\n";

        try (PreparedStatement ps = conn.prepareStatement(query)) {
            ps.setString(1, imdb);
            ResultSet rs = ps.executeQuery();
            if(rs.next() == false) {
                res.status(404);
                return "No such movie exists.";
            } else {
                year = rs.getInt("year");
                title = rs.getString("title");
            }
        } catch(SQLException e) {
            e.printStackTrace();
        }

        // Post performance
        String statement =
            "INSERT\n" +
            "INTO    performances(date, time, title, year, theater, remaining)\n" +
            "VALUES  (?, ?, ?, ?, ?, ?)";

        try (PreparedStatement ps = conn.prepareStatement(statement)) {
            ps.setString(1, date);
            ps.setString(2, time);
            ps.setString(3, title);
            ps.setInt(4, year);
            ps.setString(5, theater);
            ps.setInt(6, seats);
            ps.executeUpdate();
        } catch (SQLException e) {
            e.printStackTrace();
        }

        // Get ID and return
        query =
            "SELECT performanceId\n" +
            "FROM   performances\n" +
            "WHERE  1 = 1\n";

        try (PreparedStatement ps = conn.prepareStatement(query)) {
            ResultSet rs = ps.executeQuery();
            if (rs.next()) {
                String id = rs.getString("performanceId");
                String result = String.format("{ 'id': %s }", id);
                res.status(201);
                res.body(result);
                return String.format("%s", id);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }

        return "done?";
    }




    String postTickets(Request req, Response res) {
        String user = req.queryParams("user");
        String performance = req.queryParams("performance");
        String pwd = req.queryParams("pwd");
        String hashPwd = phg.hash(pwd);
        int seats = 0;
        int tickets = 0;

        // Authenticate user
        String query = 
        "SELECT password\n" +
        "FROM customers\n" +
        "WHERE username = ?";

        try(PreparedStatement ps = conn.prepareStatement(query)) {
            ps.setString(1, user);
            ResultSet rs = ps.executeQuery();
            if (rs.next()) {
                if(!hashPwd.equals(rs.getString("password"))) {
                    res.status(400);
                    return "Wrong password";
                }
            } else {
                return "Wrong username.";
            }
        } catch(SQLException e) {
            e.printStackTrace();
        }

        // Check if performance exists and check seats available
        query = 
        "SELECT *\n" +
        "FROM performances\n" +
        "WHERE performanceId = ?";

        try(PreparedStatement ps = conn.prepareStatement(query)) {
            ps.setString(1, performance);
            ResultSet rs = ps.executeQuery();
            if (!rs.next()) {
                return "No such performance!";
            } else {
                seats = rs.getInt("remaining");
            }
        } catch(SQLException e) {
            e.printStackTrace();
        }
        
        // Update seats
        if(seats < 1) {
            return "No tickets left";
        }

        String statement =
            "UPDATE performances\n" +
            "set    remaining = ?" +
            "WHERE  performanceId = ?";

        try (PreparedStatement ps = conn.prepareStatement(statement)) {
            ps.setInt(1, seats-1);
            ps.setString(2, performance);
            ps.executeUpdate();
        } catch (SQLException e) {
            e.printStackTrace();
        }



        // Get amount of customer tickets
        query = 
        "SELECT *\n" +
        "FROM tickets\n" +
        "WHERE username = ?";

        try(PreparedStatement ps = conn.prepareStatement(query)) {
            ps.setString(1, user);
            ResultSet rs = ps.executeQuery();
            while (rs.next()) {
                tickets++;
            }
        } catch(SQLException e) {
            e.printStackTrace();
        }

        // Update customer
        statement =
            "UPDATE customers\n" +
            "set    nbrOfTickets = ?\n" +
            "WHERE username = ?";

        try (PreparedStatement ps = conn.prepareStatement(statement)) {
            ps.setInt(1, tickets + 1);
            ps.setString(2, user);
            ps.executeUpdate();
        } catch (SQLException e) {
            e.printStackTrace();
        }


        // Post ticket
                statement =
                "INSERT\n" +
                "INTO    tickets(username, performanceId)\n" +
                "VALUES  (?, ?)";
    
            try (PreparedStatement ps = conn.prepareStatement(statement)) {
                ps.setString(1, user);
                ps.setString(2, performance);
                ps.executeUpdate();
            } catch (SQLException e) {
                e.printStackTrace();
            }

        // Get ID and return
        query =
            "SELECT t_id\n" +
            "FROM   tickets\n" +
            "WHERE  1 = 1\n";

        try (PreparedStatement ps = conn.prepareStatement(query)) {
            ResultSet rs = ps.executeQuery();
            if (rs.next()) {
                String id = rs.getString("t_id");
                String result = String.format("{ 'id': %s }", id);
                res.status(201);
                res.body(result);
                return String.format("/tickets/%s", id);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }



        return "Error";
    }

    String getCustomerTickets(Request req, Response res) {
        String id = req.params(":id");

         // Get ID and return
        String query =
            "SELECT nbrOfTickets, date, time, theater, title, year\n" +
            "FROM   customers\n" +
            "JOIN   tickets\n" +
            "USING  (username)" +
            "JOIN   performances\n" +
            "USING  (performanceId)" +
            "JOIN   movies\n" +
            "USING  (title, year)";

        try (PreparedStatement ps = conn.prepareStatement(query)) {
            ResultSet rs = ps.executeQuery();
            String result = JSONizer.toJSON(rs, "data");
            res.status(201);
            res.body(result);
            return result;
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return "Error";
    }

